demoCSS.content:
.areaChart {
  path.line {
    fill: none;
    stroke: #25a6fc;
    stroke-width: 3.5px;
  }

  path.area {
    fill: #6e6ea7;
    opacity: 0.5;
  }

  .axis {
    fill: #333;
    shape-rendering: crispEdges;
  }

  .chartTitle {
    font-weight: bold;
  }

  .x.axis .minor {
    fill: #000;
  }

  .axis path,
  .axis line {
    fill: none;
    shape-rendering: crispEdges;
    stroke: #ccc;
  }

  .y.axis line,
  .y.axis path,
  .x.axis line,
  .x.axis path {
    fill: none;
    stroke: #ccc;
  }

  .voronoi {
    fill: black;
    opacity: 0;
    stroke: black;
    stroke-width: 0.1;
  }

  .voronoi.showVoronoi {
    fill: rgba(0, 0, 0, 0);
    opacity: 1;
    stroke-width: 1;
  }

  .point {
    display: none;
    fill: #69d895;
  }
}

demoTS.content:
import { renderChart } from "./area-chart"
import { CONTAINER_ID, createChartConfig } from "./income-chart-config"
import { setupChartControls } from "./income-chart-controls"
import { IncomeItem } from "./income-item-model"

const main = async () => {
  const dataPoints = await IncomeItem.fetchAndCreateCollection()
  const chartConfig = createChartConfig(dataPoints)

  const { toggleVoronoi } = renderChart<IncomeItem>(chartConfig)

  setupChartControls(toggleVoronoi)
}

export { CONTAINER_ID }

export default main

export const BUTTON_ID = "toggle-voronoi"

export const setupChartControls = (onToggleVoronoiClick: () => void) => {
  const buttonElement = document.getElementById("toggle-voronoi") as HTMLElement

  buttonElement.addEventListener("click", (clickEvent: MouseEvent) => {
    clickEvent.preventDefault()

    onToggleVoronoiClick()
  })
}

import { csv } from "d3"

type IncomeItemBase = {
  percent: number
  year: number
}

type IncomeItemData = IncomeItemBase & {
  pointIndex: number
}

class IncomeItem {
  private readonly incomeItemData: IncomeItemData

  private constructor(incomeItemData: IncomeItemData) {
    this.incomeItemData = incomeItemData
  }

  public static async fetchAndCreateCollection(): Promise<IncomeItem[]> {
    const response = (await (csv(
      `${ROOT_PATH}data/d3js/area/data.csv`
    ) as unknown)) as IncomeItemBase[]

    const incomeItemsData = response.map((...[point, pointIndex]) => ({
      ...point,
      pointIndex,
    }))

    return incomeItemsData.map(
      (incomeItemData) => new IncomeItem(incomeItemData)
    )
  }

  public getYear() {
    return this.incomeItemData.year
  }

  public getNormalizedValue() {
    return this.incomeItemData.percent / 100
  }

  public getId() {
    return this.incomeItemData.pointIndex
  }

  public getSummary(): string {
    const { incomeItemData } = this

    return `Year: ${incomeItemData.year}, Percent: ${incomeItemData.percent}%`
  }
}

export { IncomeItem }
