files.cssModule:
.areaChart {
  path.line {
    fill: none;
    stroke: #25a6fc;
    stroke-width: 3.5px;
  }

  path.area {
    fill: #6e6ea7;
    opacity: 0.5;
  }

  .axis {
    fill: #333;
    shape-rendering: crispEdges;
  }

  .chartTitle {
    font-weight: bold;
  }

  .x.axis .minor {
    fill: #000;
  }

  .axis path,
  .axis line {
    fill: none;
    shape-rendering: crispEdges;
    stroke: #ccc;
  }

  .y.axis line,
  .y.axis path,
  .x.axis line,
  .x.axis path {
    fill: none;
    stroke: #ccc;
  }

  .voronoi {
    fill: black;
    opacity: 0;
    stroke: black;
    stroke-width: 0.1;
  }

  .voronoi.showVoronoi {
    fill: rgba(0, 0, 0, 0);
    opacity: 1;
    stroke-width: 1;
  }

  .point {
    display: none;
    fill: #69d895;
  }
}

demo.content:
import { renderChart } from "./area-chart"
import { Point, createChartConfig, fetchData } from "./area-chart-data"

const main = async () => {
  const data = await fetchData()
  const chartConfig = createChartConfig(data)

  const { toggleVoronoi } = renderChart<Point>(chartConfig)

  ;(document.getElementById("toggle-voronoi") as HTMLElement).addEventListener(
    "click",
    (e) => {
      e.preventDefault()

      toggleVoronoi()
    }
  )
}

export default main

import { csv } from "d3"

import { ChartConfig } from "./area-chart"

type PointBase = {
  percent: number
  year: number
}

export type Point = PointBase & {
  index: number
}

export const fetchData = async (): Promise<Point[]> => {
  const result = (await (csv(
    `${ROOT_PATH}data/d3js/area/data.csv`
  ) as unknown)) as PointBase[]

  return result.map((p, index) => ({
    ...p,
    index,
  }))
}

type Config = ChartConfig<Point>

const getItemXValue: Config["getItemXValue"] = (point) => point.year
const getItemYValue: Config["getItemYValue"] = (point) => point.percent / 100
const getItemId: Config["getItemId"] = (point) => point.index
const getItemTitle: Config["getItemTitle"] = (point: Point) =>
  `Year: ${point.year}, Percent: ${point.percent}%`
const getChartTitle = () =>
  "Share of top decile [aka top 10%] in national income"

export const createChartConfig = (items: Point[]): ChartConfig<Point> => ({
  getChartTitle,
  getItemId,
  getItemTitle,
  getItemXValue,
  getItemYValue,
  items,
  rootElId: "chart",
})

