import { renderChart } from "./expenses-chord-chart"
import {
  createChartConfig,
  createInitialState,
  setupChartForm,
} from "./expenses-chord-chart-data"
import { Expenses } from "./expenses-chord-data-model"

const main = async () => {
  const expenses = await Expenses.fetchAndCreate()

  const state = createInitialState()
  const chartConfig = createChartConfig({
    expenses,
    state,
  })

  const { renderItems } = renderChart(chartConfig)

  setupChartForm({
    expenses,
    renderItems,
    state,
  })
}

export default main

import { ChartConfig, DisplayType, RibbonType } from "./expenses-chord-chart"
import { Expenses } from "./expenses-chord-data-model"

type State = {
  selectedCountry: string
  selectedRegion: string
  timeIndex: number
}

export const createInitialState = (): State => ({
  selectedCountry: Expenses.ALL_ID,
  selectedRegion: Expenses.ALL_ID,
  timeIndex: 0,
})

export const createChartConfig = ({
  expenses,
  state,
}: {
  expenses: Expenses
  state: State
}): ChartConfig => {
  const getChordMatrix: ChartConfig["getChordMatrix"] = () =>
    expenses.getRelationsMatrix({
      countryFilter: state.selectedCountry,
      regionFilter: state.selectedRegion,
      timeIndexFilter: state.timeIndex,
    })

  const allNames = expenses.getAllNames()

  const getChordTitle: ChartConfig["getChordTitle"] = (
    ...[sourceIndex, targetIndex, sourceValue]
  ) =>
    `People from "${allNames[sourceIndex]}" spend into "${allNames[targetIndex]}": ${sourceValue}`

  const getChordGroupTitle: ChartConfig["getChordGroupTitle"] = (
    chordItemLabel
  ) => chordItemLabel

  const getRibbonGroupIdColor: ChartConfig["getRibbonGroupIdColor"] = (
    ...[sourceGroupId, targetGroupId]
  ) =>
    state.selectedRegion === Expenses.ALL_ID ? targetGroupId : sourceGroupId

  const getDisplayTypeOnGroupClick: ChartConfig["getDisplayTypeOnGroupClick"] = (
    chordGroupId
  ) =>
    expenses.getCountriesList().includes(chordGroupId)
      ? DisplayType.Source
      : DisplayType.Target

  const getRibbonType: ChartConfig["getRibbonType"] = () =>
    state.selectedCountry !== Expenses.ALL_ID &&
    state.selectedRegion !== Expenses.ALL_ID
      ? RibbonType.Common
      : RibbonType.Arrow

  return {
    chordGroupsIds: allNames,
    getChordGroupTitle,
    getChordMatrix,
    getChordTitle,
    getDisplayTypeOnGroupClick,
    getRibbonGroupIdColor,
    getRibbonType,
    rootElId: "chart",
  }
}

export const setupChartForm = ({
  expenses,
  renderItems,
  state,
}: {
  expenses: Expenses
  renderItems: () => void
  state: State
}) => {
  $("#slider-time").slider({
    change: (...[, { value: timeValue }]) => {
      if (timeValue === 3) {
        // @TODO: error in this case, find why
        return
      }

      state.timeIndex = timeValue!
      renderItems()
    },
    max: expenses.getTimeFramesNumber(),
    min: 0,
  })

  const setupSelect = ({
    id,
    onChange,
    selectOptions,
  }: {
    id: string
    onChange: (v: string) => void
    selectOptions: string[]
  }) => {
    const selectEl = document.getElementById(id) as HTMLSelectElement

    ;[Expenses.ALL_ID].concat(selectOptions).forEach((selectOption) => {
      const option = document.createElement("option")

      option.setAttribute("value", selectOption)
      option.innerText = selectOption

      selectEl.appendChild(option)
    })

    selectEl.addEventListener("change", () => {
      onChange(selectEl.value)
    })
  }

  setupSelect({
    id: "countries-select",
    onChange: (newSelected: string) => {
      state.selectedCountry = newSelected
      renderItems()
    },
    selectOptions: expenses.getCountriesList(),
  })
  setupSelect({
    id: "regions-select",
    onChange: (newSelected: string) => {
      state.selectedRegion = newSelected
      renderItems()
    },
    selectOptions: expenses.getRegionsList(),
  })
}

import {
  BaseType,
  Chord,
  ChordGroup,
  Selection,
  arc as arcD3,
  chordDirected,
  descending as descendingD3,
  easeCircle,
  interpolate,
  ribbonArrow as ribbonArrowD3,
  ribbon as ribbonD3,
  scaleOrdinal,
  schemeTableau10,
  select,
  zoom,
} from "d3"
import { v1 as uuid } from "uuid"

import * as styles from "./expenses-chord.module.css"

const height = 800
const elementDefaultOpacity = 0.7

export enum DisplayType {
  Source = "source",
  Target = "target",
}

export enum RibbonType {
  Arrow = "arrow",
  Common = "common",
}

type ChartState = {
  lastFocused: string | null
}

const durations = {
  ribbonAnimation: 1000,
  zoom: 500,
} as const

const easingFn = easeCircle

const applyOpacityEffect = <ContainerEl extends BaseType, Datum>(
  elementsSelection: Selection<ContainerEl, Datum, BaseType, unknown>
) => {
  elementsSelection
    .style("opacity", elementDefaultOpacity)
    .on("mouseenter", function () {
      select(this).style("opacity", 1)
    })
    .on("mouseleave", function () {
      select(this).style("opacity", elementDefaultOpacity)
    })
}

// eslint-disable-next-line max-params,@typescript-eslint/no-explicit-any
const zoomed = function (this: Element, zoomEvent: any) {
  select(this)
    .transition()
    .duration(durations.zoom)
    .attr("transform", zoomEvent.transform)
}

export type ChartConfig = {
  chordGroupsIds: string[]
  getChordGroupTitle: (groupId: string) => string
  getChordMatrix: () => number[][]
  getChordTitle: (
    sourceIndex: number,
    targetIndex: number,
    sourceValue: number,
    targetValue: number
  ) => string
  getDisplayTypeOnGroupClick: (chordGroupId: string) => DisplayType
  getRibbonGroupIdColor: (
    sourceGroupId: string,
    targetGroupId: string
  ) => string
  getRibbonType: () => RibbonType
  rootElId: string
}

export const renderChart = (chartConfig: ChartConfig) => {
  const { rootElId } = chartConfig

  const chartState: ChartState = {
    lastFocused: null,
  }

  const { chordGroupsIds } = chartConfig

  const color = scaleOrdinal(chordGroupsIds, schemeTableau10)

  const { width } = (document.getElementById(
    rootElId
  ) as HTMLElement).getBoundingClientRect()

  const innerRadius = Math.min(width, height) * 0.5 - 20
  const outerRadius = innerRadius + 20

  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const ribbonCommon = (r: any) =>
    r.radius(innerRadius - 0.5).padAngle(1 / innerRadius)

  const ribbonArrow = ribbonCommon(ribbonArrowD3())
  const ribbon = ribbonCommon(ribbonD3())

  const zoomBehavior = zoom()
    .extent([
      [0, 0],
      [width / 2, height / 2],
    ])
    .on("end", zoomed)

  const totalHeight = height + 50

  const svg = select(`#${rootElId}`)
    .attr("class", styles.chartWrapper)
    .append("svg")
    .attr("width", width)
    .attr("height", totalHeight)
    .append("g")
    .attr("transform", `translate(${width / 2}, ${totalHeight / 2})`)
    .append("g")
    .call(zoomBehavior as any) // eslint-disable-line @typescript-eslint/no-explicit-any
    .on("dblclick.zoom", null)

  // this rect is to allow zooming
  svg
    .append("rect")
    .attr("fill", "#fff")
    .attr("width", width)
    .attr("height", height)
    .attr("transform", `translate(-${width / 2}, -${totalHeight / 2})`)

  const textId = uuid()

  const chord = chordDirected()
    .padAngle(12 / innerRadius)
    .sortSubgroups(descendingD3)
    .sortChords(descendingD3)

  svg
    .append("path")
    .attr("fill", "none")
    .attr("id", textId)
    .attr(
      "d",
      arcD3()({
        endAngle: 2 * Math.PI,
        innerRadius: 0,
        outerRadius,
        startAngle: 0,
      }) as string
    )

  const arc = arcD3<d3.ChordGroup>()
    .innerRadius(innerRadius)
    .outerRadius(outerRadius)

  const ribbonContainer = svg.append("g")
  const groupContainer = svg
    .append("g")
    .attr("font-family", "sans-serif")
    .attr("font-size", 10)

  const getRibbonKey = (ribbonNode: Chord) =>
    `${ribbonNode.source.index}_${ribbonNode.target.index}`

  const renderItems = () => {
    const usedRibbon =
      chartConfig.getRibbonType() === RibbonType.Common ? ribbon : ribbonArrow

    const matrix = chartConfig.getChordMatrix()
    const chords = chord(matrix)

    const initialRibbonsData = ribbonContainer
      .selectAll<SVGPathElement, Chord>(`.${styles.ribbon}`)
      .data()
      .reduce((...[acc, ribbonNode]) => {
        acc[getRibbonKey(ribbonNode)] = ribbonNode

        return acc
      }, {} as { [k: string]: Chord | undefined })

    const fillRibbon = (chordItem: Chord) =>
      color(
        chartConfig.getRibbonGroupIdColor(
          chordGroupsIds[chordItem.source.index],
          chordGroupsIds[chordItem.target.index]
        )
      )

    const ribbons = ribbonContainer
      .selectAll<SVGPathElement, Chord>(`.${styles.ribbon}`)
      .data<Chord>(chords, (chordItem) => getRibbonKey(chordItem))
      .join(
        (enter) => {
          const enterSelection = enter
            .append("path")
            .attr("class", styles.ribbon)
            .attr("fill", fillRibbon)

          applyOpacityEffect(enterSelection)

          return enterSelection
            .transition()
            .duration(durations.ribbonAnimation)
            .attrTween("d", (finalRibbon) => {
              const initialRibbon = {
                source: {
                  endAngle: 0,
                  startAngle: 0,
                },
                target: {
                  endAngle: 0,
                  startAngle: 0,
                },
              }
              const interpolateSource = interpolate(
                initialRibbon.source,
                finalRibbon.source
              )
              const interpolateTarget = interpolate(
                initialRibbon.target,
                finalRibbon.target
              )

              return (t) => {
                const interpolated = {
                  source: interpolateSource(t),
                  target: interpolateTarget(t),
                }

                return usedRibbon(interpolated)
              }
            })
        },
        (update) => {
          update
            .transition()
            .duration(durations.ribbonAnimation)
            .attr("fill", fillRibbon)
            .attrTween("d", (finalRibbon) => {
              const {
                [getRibbonKey(finalRibbon)]: initialRibbon,
              } = initialRibbonsData

              if (!initialRibbon) {
                return () => usedRibbon(finalRibbon)
              }

              const interpolateSource = interpolate(
                initialRibbon.source,
                finalRibbon.source
              )
              const interpolateTarget = interpolate(
                initialRibbon.target,
                finalRibbon.target
              )

              return (t) => {
                const interpolated = {
                  source: interpolateSource(t),
                  target: interpolateTarget(t),
                }

                return usedRibbon(interpolated)
              }
            })

          return update
        }
      )

    ribbons
      .attr("title", (chordItem) =>
        chartConfig.getChordTitle(
          chordItem.source.index,
          chordItem.target.index,
          chordItem.source.value,
          chordItem.target.value
        )
      )
      .on("click", function (...[, chordItem]) {
        const chordSelection = select(this)
        const chordGroupId = `${chordGroupsIds[chordItem.source.index]}_${
          chordGroupsIds[chordItem.target.index]
        }`

        if (chartState.lastFocused === chordGroupId) {
          ribbons.attr("display", "block")
          chartState.lastFocused = null
        } else {
          ribbons.attr("display", "none")
          chordSelection.attr("display", "block")
          chartState.lastFocused = chordGroupId
        }
      })

    $(`.${styles.ribbon}`).tooltip({
      track: true,
    })

    const getGroupText = (chordItem: ChordGroup) => {
      if (chordItem.endAngle - chordItem.startAngle < 0.07) {
        return ""
      }

      return chartConfig.getChordGroupTitle(chordGroupsIds[chordItem.index])
    }

    const initialGroupData = groupContainer
      .selectAll(`.${styles.chordGroup}`)
      .data()

    groupContainer
      .selectAll<SVGGElement, ChordGroup>(`.${styles.chordGroup}`)
      .data<ChordGroup>(chords.groups, (chordGroup) => chordGroup.index)
      .join(
        (enter) => {
          const groupSelection = enter
            .append("g")
            .attr("class", styles.chordGroup)
            .attr("title", (groupItem) =>
              chartConfig.getChordGroupTitle(chordGroupsIds[groupItem.index])
            )

          applyOpacityEffect(groupSelection)

          groupSelection
            .append("path")
            .attr("class", "group-path")
            .transition()
            .duration(durations.ribbonAnimation)
            .ease(easingFn)
            .attrTween("d", (finalGroup) => {
              const interpolateFn = interpolate<ChordGroup>(
                {
                  ...finalGroup,
                  endAngle: 0,
                  startAngle: 0,
                },
                finalGroup
              )

              return (t) => arc(interpolateFn(t))!
            })
            .attr("fill", (groupItem) => color(chordGroupsIds[groupItem.index]))
            .attr("stroke", "#fff")

          groupSelection
            .append("text")
            .attr("dy", -3)
            .append("textPath")
            .attr("xlink:href", `#${textId}`)
            .attr("class", styles.groupText)
            .text(getGroupText)
            .transition()
            .duration(durations.ribbonAnimation)
            .ease(easingFn)
            .attr(
              "startOffset",
              (groupItem) => groupItem.startAngle * outerRadius
            )

          groupSelection.on("click", (...[, groupItem]) => {
            const { [groupItem.index]: chordGroupId } = chordGroupsIds
            const latestRibbons = ribbonContainer.selectAll<
              SVGPathElement,
              Chord
            >(`.${styles.ribbon}`)

            if (chartState.lastFocused === chordGroupId) {
              latestRibbons.attr("display", () => "block")
              chartState.lastFocused = null

              return
            }

            chartState.lastFocused = chordGroupId

            if (
              chartConfig.getDisplayTypeOnGroupClick(chordGroupId) ===
              DisplayType.Source
            ) {
              latestRibbons.attr("display", (otherGroupItem) =>
                otherGroupItem.source.index === groupItem.index
                  ? "block"
                  : "none"
              )

              return
            }

            latestRibbons.attr("display", (otherGroupItem) =>
              otherGroupItem.target.index === groupItem.index ? "block" : "none"
            )
          })

          $(`.${styles.chordGroup}`).tooltip({
            track: true,
          })

          return groupSelection
        },
        (update) => {
          update
            .select(".group-path")
            .transition()
            .duration(durations.ribbonAnimation)
            .attrTween("d", (...[finalGroup, finalGroupIndex]) => {
              const { [finalGroupIndex]: initialGroup } = initialGroupData
              const interpolateFn = interpolate(initialGroup, finalGroup)

              return (t) => arc(interpolateFn(t)) as string
            })

          update
            .select(`.${styles.groupText}`)
            .text(getGroupText)
            .transition()
            .duration(durations.ribbonAnimation)
            .attr(
              "startOffset",
              (groupItem) => groupItem.startAngle * outerRadius
            )

          return update
        },
        (exit) => exit.remove()
      )
      .attr("class", styles.chordGroup)
  }

  renderItems()

  return {
    renderItems,
  }
}

import { json } from "d3"

type DataItem = {
  value: number | null // eslint-disable-line id-denylist
  year: number
}

/**
 * The data types are encapsulated and not shared
 */
type ExpensesData = {
  [country: string]: {
    [region: string]: DataItem[]
  }
}

class Expenses {
  public static ALL_ID = "All"

  private readonly countries: string[]
  private readonly expensesData: ExpensesData
  private readonly names: string[]
  private readonly regions: string[]

  private constructor(expensesData: ExpensesData) {
    const countries = Object.keys(expensesData).sort()

    this.countries = countries
    this.regions = Object.keys(expensesData[countries[0]]).sort()
    this.expensesData = expensesData
    this.names = countries.concat(this.regions)
  }

  public static async fetchAndCreate(): Promise<Expenses> {
    const expensesData = (await (json(
      `${ROOT_PATH}data/d3js/expenses-chord/data.json`
    ) as unknown)) as ExpensesData

    return new Expenses(expensesData)
  }

  public getRelationsMatrix({
    countryFilter,
    regionFilter,
    timeIndexFilter,
  }: {
    countryFilter: string
    regionFilter: string
    timeIndexFilter: number
  }): number[][] {
    const { expensesData, names } = this

    return names.map((maybeCountry) => {
      if (!expensesData[maybeCountry] as unknown) {
        return names.map(() => 0)
      }

      if (![Expenses.ALL_ID, maybeCountry].includes(countryFilter)) {
        return names.map(() => 0)
      }

      return names.map((maybeRegion) => {
        const {
          [maybeCountry]: { [maybeRegion]: dataItem },
        } = expensesData

        if (!dataItem as unknown) {
          return 0
        }

        if (![Expenses.ALL_ID, maybeRegion].includes(regionFilter)) {
          return 0
        }

        return dataItem[timeIndexFilter].value
      })
    }) as number[][]
  }

  public getTimeFramesNumber(): number {
    const { countries, expensesData, regions } = this

    return expensesData[countries[0]][regions[0]].length - 1
  }

  public getCountriesList(): Expenses["countries"] {
    return this.countries.slice()
  }

  public getRegionsList(): Expenses["regions"] {
    return this.regions.slice()
  }

  public getAllNames(): Expenses["names"] {
    return this.names.slice()
  }
}

export { Expenses }
