.mareysScheduleChart {
  svg {
    font: 10px sans-serif;
  }

  .axis {
    path {
      display: none;
    }

    line {
      shape-rendering: crispEdges;
      stroke: #000;
    }
  }

  .chartTitle {
    font-size: 15px;
  }

  .horizontalMarker {
    line {
      shape-rendering: crispEdges;
      stroke: #ddd;
      stroke-dasharray: 1, 1;
    }

    text {
      text-anchor: end;
    }
  }

  .line {
    path {
      fill: none;
      stroke-width: 2.5px;
    }

    circle {
      stroke: #fff;
    }

    .blackLine {
      path {
        stroke: rgb(34 34 34);
      }

      circle {
        fill: rgb(34 34 34);
      }
    }

    .orangeLine {
      path {
        stroke: #d9b611;
      }

      circle {
        fill: rgb(183 116 9);
      }
    }

    .redLine {
      path {
        stroke: rgb(192 62 29);
      }

      circle {
        fill: rgb(192 62 29);
      }
    }
  }
}

demoTS.content:
import { renderChart } from "./crossing-lines-chart"
import { createChartConfig } from "./mareys-schedule-chart-config"
import {
  CONTAINER_ID,
  RANGE_ID,
  setupControls,
} from "./mareys-schedule-controls"
import { MareysSchedules } from "./mareys-schedule-data-model"

// @TODO: Add interaction on click

const main = async () => {
  const schedules = await MareysSchedules.fetchAndCreateSchedules()

  const chartConfig = createChartConfig(schedules)

  const { refresh: updateScheduleLimits } = renderChart(chartConfig)

  setupControls(updateScheduleLimits)
}

export { CONTAINER_ID, RANGE_ID }

export default main

import qs from "query-string"

import {
  ChartConfig,
  HorizontalMarker,
  LineStyle,
} from "./crossing-lines-chart"
import { CONTAINER_ID } from "./mareys-schedule-controls"
import { MareysSchedules } from "./mareys-schedule-data-model"

const formatAMPM = (date: Date) => {
  let hours = date.getHours()
  const ampm = hours >= 12 ? "PM" : "AM"

  hours = hours % 12
  hours = hours ? hours : 12

  const minutes = date.getMinutes()
  const minutesStr = minutes < 10 ? `0${minutes}` : minutes.toString()

  return `${hours}:${minutesStr} ${ampm}`
}

export const createChartConfig = (schedules: MareysSchedules): ChartConfig => {
  const stations = schedules.getStations()
  const horizontalMarkers: HorizontalMarker[] = stations.map((station) => ({
    key: station.key,
    label: station.name,
    position: station.distance,
  }))

  const horizontalMarkerMap = horizontalMarkers.reduce<{
    [key: string]: HorizontalMarker
  }>((...[acc, horizontalMarker]) => {
    acc[horizontalMarker.key] = horizontalMarker

    return acc
  }, {})

  const trains = schedules.getTrains()

  const lines = trains.map((train) => ({
    id: train.id,
    points: train.stops.map((stop) => ({
      horizontalMarker: horizontalMarkerMap[stop.station.key],
      lineId: stop.trainId,
      x: stop.time,
    })),
  }))

  const trainsMap = schedules.getTrainsMap()

  const getLineTitle: ChartConfig["getLineTitle"] = (line) => {
    const { [line.id]: trainData } = trainsMap

    return MareysSchedules.getTrainTitle(trainData)
  }

  const getPointTitle: ChartConfig["getPointTitle"] = (point) => {
    const { [point.lineId]: train } = trainsMap

    return `${MareysSchedules.getTrainTitle(train)}\n${
      point.horizontalMarker.label
    } at ${formatAMPM(point.x!)}`
  }

  const getLineStyle: ChartConfig["getLineStyle"] = (line) => {
    const { [line.id]: train } = trainsMap

    switch (train.type) {
      case "B":
        return LineStyle.Red
      case "L":
        return LineStyle.Black
      case "N":
        return LineStyle.Orange

      default:
        return LineStyle.Red
    }
  }

  const crossingLinesData: ChartConfig["crossingLinesData"] = {
    horizontalMarkers,
    lines,
  }

  const chartTitle = "E.J. Mareyâ€™s graphical train schedule (4:30AM - 1:30AM)"

  const getXAxisLabel: ChartConfig["getXAxisLabel"] = (date) =>
    MareysSchedules.convertDateToString(date)

  const onPointClick: ChartConfig["onPointClick"] = (point) => {
    window.open(
      `https://www.google.com/search?${qs.stringify({
        q: `Station California ${point.horizontalMarker.label}`,
      })}`
    )
  }

  return {
    chartTitle,
    crossingLinesData,
    getLineStyle,
    getLineTitle,
    getPointTitle,
    getXAxisLabel,
    onPointClick,
    rootElId: CONTAINER_ID,
  }
}

import { Redraw } from "./crossing-lines-chart"
import { MareysSchedules } from "./mareys-schedule-data-model"

type ScheduleLimits = [number, number]

const CONTAINER_ID = "chart"

const RANGE_ID = "schedule-range"

const convertLimitsToTimes = (limits: ScheduleLimits): [string, string] => {
  const times: string[] = []

  limits.forEach((limit) => {
    const wholeMinutes = (limit / 100) * 1200

    let fragment = "AM"
    let hours = Math.floor(wholeMinutes / 60)
    let minutes = Math.floor(wholeMinutes % 60)

    if (minutes > 30) {
      minutes = minutes - 30
      hours = hours + 1
    } else {
      minutes = minutes + 30
    }

    hours = hours + 4

    if (hours > 23) {
      if (hours === 24) {
        hours = hours - 12
      } else {
        hours = hours - 24
      }
    } else if (hours > 11) {
      fragment = "PM"

      if (hours !== 12) {
        hours = hours - 12
      }
    }

    const minutesStr = minutes < 10 ? `0${minutes}` : minutes.toString()

    const finalTime = `${hours}:${minutesStr}${fragment}`

    return times.push(finalTime)
  })

  return times as [string, string]
}

const setupControls = (updateScheduleLimits: Redraw) => {
  const slider = $(`#${RANGE_ID}`)

  slider.slider({
    change: () => {
      const limits = slider.slider("values") as ScheduleLimits
      const timeLimits = convertLimitsToTimes(limits).map(
        MareysSchedules.convertStringToDate
      ) as [Date, Date]

      updateScheduleLimits(timeLimits)
    },
    range: true,
  })

  slider.slider("values", [10, 50])
}

export { CONTAINER_ID, RANGE_ID, setupControls }

import { timeFormat, timeParse, tsv } from "d3"

const timeFormatStr = "%I:%M%p"

const getParseTime = () => timeParse(timeFormatStr)

const parseTime = (timeStr: string): Date => {
  const timeDate = getParseTime()(timeStr)

  if (timeDate !== null && timeDate.getHours() < 3) {
    timeDate.setDate(timeDate.getDate() + 1)
  }

  return timeDate!
}

type TrainType = "B" | "L" | "N"

type RawDataItem = {
  [stop: string]: string
  direction: string
  number: string
  type: TrainType
}

type Station = {
  distance: number
  key: string
  name: string
  zone: number
}

type Stop = {
  station: Station
  time: Date | null
  trainId: number
}

type Train = {
  direction: string
  id: number
  number: string
  stops: Stop[]
  type: TrainType
}

type SchedulesData = {
  stations: Station[]
  trains: Train[]
}

class MareysSchedules {
  private readonly schedulesData: SchedulesData
  private constructor(schedulesData: SchedulesData) {
    this.schedulesData = schedulesData
  }

  public static async fetchAndCreateSchedules(): Promise<MareysSchedules> {
    const originalItems = (await tsv(
      `${ROOT_PATH}data/d3js/mareys-schedule/data.tsv`
    )) as unknown as RawDataItem[]

    const stations: Station[] = []

    const trains: Train[] = originalItems
      .map((...[train, trainIndex]) => {
        if (trainIndex === 0) {
          for (const key in train) {
            if (/^stop\|/.test(key)) {
              const stopFragments = key.split("|")

              stations.push({
                distance: +stopFragments[2],
                key,
                name: stopFragments[1],
                zone: +stopFragments[3],
              })
            }
          }
        }

        return {
          direction: train.direction,
          id: trainIndex,
          number: train.number,
          stops: stations
            .map((station) => ({
              station,
              time: parseTime(train[station.key]),
              trainId: trainIndex,
            }))
            .filter((station) => (station.time as unknown) !== null),
          type: train.type,
        }
      })
      .filter((train) => /[NLB]/.test(train.type))

    const schedulesData = {
      stations,
      trains,
    }

    return new MareysSchedules(schedulesData)
  }

  public static convertDateToString(date: Date): string {
    return timeFormat(timeFormatStr)(date)
  }

  public static convertStringToDate(dateStr: string): Date {
    return parseTime(dateStr)
  }

  public static getTrainTitle(trainData: Train): string {
    const {
      stops: { 0: firstStop, [trainData.stops.length - 1]: lastStop },
    } = trainData

    if (trainData.direction === "S") {
      return `${firstStop.station.name} -> ${lastStop.station.name}`
    }

    return `${lastStop.station.name} -> ${firstStop.station.name}`
  }

  public getStations() {
    return this.schedulesData.stations.slice()
  }

  public getTrains() {
    return this.schedulesData.trains.slice()
  }

  public getTrainsMap() {
    return this.schedulesData.trains.reduce<{ [id: string]: Train }>(
      (...[acc, train]) => {
        acc[train.id] = train

        return acc
      },
      {}
    )
  }
}

export { MareysSchedules }

